import React from 'react';

function Icon({ name }) {
  const iconMap = {
    'menu': '3 dot menu',
    'add': 'add',
    'backlog': 'Backlog',
    'cancelled': 'Cancelled',
    'display': 'Display',
    'done': 'Done',
    'down': 'down',
    'high-priority': 'Img - High Priority',
    'low-priority': 'Img - Low Priority',
    'medium-priority': 'Img - Medium Priority',
    'in-progress': 'in-progress',
    'no-priority': 'No-priority',
    'urgent-priority': 'SVG - Urgent Priority colour',
    'urgent-priority-grey': 'SVG - Urgent Priority grey',
    'todo': 'To-do'
  };

  const iconFileName = iconMap[name] || name;

  return (
    <img 
      src={require(`../assets/${iconFileName}.svg`)}
      alt={`${name} icon`} 
      className="icon" 
    />
  );
}

export default Icon;
